  }, []);
  
  return (
    <AuthContext.Provider
      value={{
        auth,
        setAuth,
        availableProgrammersList,
        setAvailableProgrammersList,
        bugsList,
        setBugsList,
        filterList,
        setFilterList,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
