import { Card, Button, Row } from "react-bootstrap";
import { useEffect, useState } from "react";

const Filterbar = ({ setSeverity }) => {
  const [currentSeverity, setCurrentSeverity] = useState("");

  return (
    <>
      <Card border="secondary" bg={"light"}>
        <Card.Header as="h1">Severity</Card.Header>
        <Card.Body>
          <Row style={{ width: "100%", textAlign: "center" }}>
            <Button
              className="m-2"
              variant="primary"
              value={""}
              style={{ opacity: currentSeverity === "" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              All
            </Button>
            <Button
              className="m-2"
              variant="danger"
              value={"critical"}
              style={{ opacity: currentSeverity === "critical" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              Critical
            </Button>
            <Button
              className="m-2"
              variant="warning"
              value={"moderate"}
              style={{ opacity: currentSeverity === "moderate" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              Moderate
            </Button>
            <Button
              className="m-2"
              variant="success"
              value={"minor"}
              style={{ opacity: currentSeverity === "minor" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              Minor
            </Button>
          </Row>
        </Card.Body>
      </Card>
      <Card border="secondary" bg={"light"}>
        <Card.Header as="h1">Severity</Card.Header>
        <Card.Body>
          <Row style={{ width: "100%", textAlign: "center" }}>
            <Button
              className="m-2"
              variant="primary"
              value={""}
              style={{ opacity: currentSeverity === "" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              All
            </Button>
            <Button
              className="m-2"
              variant="danger"
              value={"critical"}
              style={{ opacity: currentSeverity === "critical" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              Critical
            </Button>
            <Button
              className="m-2"
              variant="warning"
              value={"moderate"}
              style={{ opacity: currentSeverity === "moderate" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              Moderate
            </Button>
            <Button
              className="m-2"
              variant="success"
              value={"minor"}
              style={{ opacity: currentSeverity === "minor" ? "1" : "0.75" }}
              onClick={(e) => {
                setSeverity(e.target.value);
                setCurrentSeverity(e.target.value);
                console.log(e.target.value);
              }}
            >
              Minor
            </Button>
          </Row>
        </Card.Body>
      </Card>
    </>
  );
};

export default Filterbar;
