import { Form, Button, Row, Col, FloatingLabel} from 'react-bootstrap';
import {useContext, useEffect, useState} from 'react'

const UserForm = () => {
    const bugList = [
        {id:1, bugName:'Crash',clicked: false},
        {id:2, bugName:'Functional error',clicked: false},
        {id:3, bugName:'Typos',clicked: false},
        {id:4, bugName:'Missing command',clicked: false},
        {id:5, bugName:'Calculation error',clicked: false},
        {id:6, bugName:'Control flow error',clicked: false}
    ]

    const [firstName, setFirstName]=useState('')
    const [lastName, setLastName]=useState('')
    const [date, setDate] = useState('')
    const [browser, setBrowser] = useState('')
    const [os, setOS] = useState('')
    const [URL, setURL] = useState('')
    const [bugs, setBugs] = useState(bugList)
    const [message, setMessage] = useState('')
    const [procedure, setProcedure] = useState('')
    const [pref, setPref] = useState({
        webcam: false
    });

    useEffect(() => {
        console.log(pref.webcam);
    }, [pref]);

    const convertTime=(str)=> {
        var date = new Date(str),
            mnth = ("0" + (date.getMonth() + 1)).slice(-2),
            day = ("0" + date.getDate()).slice(-2);
        return [date.getFullYear(), mnth, day].join("-");
        }

    const changeBugList = (e)=>{
        console.log(e.target)
        
        const {name, clicked}=e.target;
        e.target.clicked=!clicked
        console.log(name)
        console.log(clicked)

        // let newList = [...bugs]; 
        // newList[e.target.id].clicked = e.target.clicked;

        // setBugs(newList);
        // console.log(bugs)
    }

    const addBug=(e,addBug)=>{

        setBugs([...bugs, addBug]);
        console.log(bugs)
    }

    const deleteBug=(deleteBug)=>{
        console.log("1 here")
        const filterBugs = bugs.filter(bug=>{return bug!==deleteBug});
        setBugs(filterBugs)
        console.log(bugs)
        
    }

    return (
        <Form className="mx-auto mt-5" style={{width: "680px"}}>
            <Row className="mb-3">
                <Form.Group as={Col}>
                    <Form.Label>First Name</Form.Label>
                    <Form.Control 
                        type="text"
                        placeholder="Enter your First Name"
                        value={firstName} 
                        onChange={(e)=>setFirstName(e.target.value)}
                        />
                </Form.Group>
                <Form.Group as={Col}>
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control 
                        type="text"
                        placeholder="Enter your Last Name"
                        value={lastName} 
                        onChange={(e)=>setLastName(e.target.value)}
                        />
                </Form.Group>
            </Row>

            <Row className="mb-3">
                <Form.Group>
                    <Form.Label>Bug Discover Date:</Form.Label>
                    <Form.Control  
                        type="date"
                        value={date} 
                        onChange={(e)=>setDate(convertTime(e.target.value))}
                    />
                </Form.Group>
            </Row>

            <Row className="mb-3">
                <Form.Group  as={Col}>
                    <Form.Label>
                        Brower
                    </Form.Label>
                    <Form.Select onChange={(e)=>setBrowser(e.target.value)}>
                        <option value='N/A'>Select your Brower</option>
                        <option value="Google Chrome">Google Chrome</option>
                        <option value="Internet Exploer">Internet Exploer</option>
                        <option value="Safari">Safari</option>
                        <option value="Firefox">Firefox</option>
                        <option value="Microsoft Edge">Microsoft Edge</option>
                        <option value="Opera">Opera</option>
                    </Form.Select>
                </Form.Group>
                <Form.Group  as={Col}>
                    <Form.Label>Operating System</Form.Label>
                    <Form.Select onChange={(e)=>setOS(e.target.value)}>
                        <option value='N/A'>Select your Operating System</option>
                        <option value="Window 11">Window 11</option>
                        <option value="Window 10">Window 10</option>
                        <option value="Mac Os">Mac Os</option>
                        <option value="Window 7">Window 7</option>
                        <option value="Linux">Linux</option>
                    </Form.Select>
                </Form.Group>
            </Row>

            <Row className="mb-4">
                <Form.Group as={Col}>
                    <Form.Label>Gug URL</Form.Label>
                    <Form.Control 
                        type="text"
                        placeholder="Enter the URL"
                        value={URL} 
                        onChange={(e)=>setURL(e.target.value)}
                        />
                </Form.Group>
            </Row>

            <Row className="mb-3">
                <Form.Label>Software Bug</Form.Label>
                <Row className="mb-1 ">
                    <Form.Group as={Col}>
                        <Form.Check 
                            type="checkbox"
                            label='Crash'
                            name="Crash"
                            defaultChecked={pref.webcam}
                            onChange={(e)=>{
                                changeBugList(e)
                        }}
                        />
                    </Form.Group>
                    <Form.Group as={Col}>
                        <Form.Check 
                            type="checkbox"
                            label='Functional error'
                            onChange={(e)=>{
                                if(e.target.checked)
                                    addBug('Functional error')
                                else
                                    deleteBug('Functional error')
                        }}
                        />
                    </Form.Group>
                    <Form.Group as={Col}>
                        <Form.Check 
                            type="checkbox"
                            label='Typos'
                        />
                    </Form.Group>
                </Row>
                <Row className="mb-1">
                    <Form.Group as={Col}>
                        <Form.Check 
                            type="checkbox"
                            label='Missing command'
                    />
                    </Form.Group>
                    <Form.Group as={Col}>
                        <Form.Check 
                            type="checkbox"
                            label='Calculation error'
                    />
                    </Form.Group>
                    <Form.Group as={Col}>
                        <Form.Check 
                            type="checkbox"
                            label='Control flow error'
                    />
                    </Form.Group>
                </Row>
            </Row>

            <Row className="mb-4">
                <Form.Group>
                    <Form.Label>Error Message</Form.Label>
                    <FloatingLabel controlId="floatingTextarea2" label="Message">
                        <Form.Control 
                            as="textarea"
                            placeholder="Enter the Message"
                            value={message} 
                            onChange={(e)=>setMessage(e.target.value)}
                            style={{ height: '100px' }}
                        />
                    </FloatingLabel>
                </Form.Group>
            </Row>

            <Row className="mb-3">
                <Form.Group>
                    <Form.Label>Please describe any procedure to replicate this bug</Form.Label>
                    <FloatingLabel controlId="floatingTextarea2" label="Procedure">
                        <Form.Control 
                            as="textarea"
                            placeholder="Enter the URL"
                            value={procedure} 
                            onChange={(e)=>setProcedure(e.target.value)}
                            style={{ height: '200px' }}
                        />
                    </FloatingLabel>
                </Form.Group>
            </Row>
            <Button variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    )
}

export default UserForm
