import { Modal, Button, Form } from "react-bootstrap";
import { useState, useEffect } from "react";
import useAuth from "../hooks/useAuth";

const VerticallyCenteredModal = ({
  show,
  onHide,
}) => {
  const { availableProgrammersList } = useAuth();
  const [programmer, setProgrammer] = useState([]);

  useEffect(() => {
    setProgrammer(availableProgrammersList);
  }, []);

  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
    >
      <Modal.Header closeButton>
        <Modal.Title>Edit Bugs</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Change the sererity:</Form.Label>
            <Form.Select
              onChange={(e) => setProgrammer(e.target.value)}
              required
            >
              <option value="Critical">Critical</option>
              <option value="Moderate">Moderate</option>
              <option value="Minor">Minor</option>
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Select a programmer for this bug:</Form.Label>
            <Form.Select
              onChange={(e) => setProgrammer(e.target.value)}
              required
            >
              <option value="">Select a Programmer</option>
              {availableProgrammersList.map((availableProgrammer) => (
                <option key={availableProgrammer.id}>
                  {availableProgrammer.firstName}{" "}
                  {availableProgrammer.firstName}
                </option>
              ))}
              {/* <option value="">Select a Programmer</option>
              <option value="Google Chrome">Google Chrome</option>
              <option value="Internet Exploer">Internet Exploer</option>
              <option value="Safari">Safari</option>
              <option value="Firefox">Firefox</option>
              <option value="Microsoft Edge">Microsoft Edge</option>
              <option value="Opera">Opera</option> */}
            </Form.Select>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Close
        </Button>
        <Button variant="primary">Save changes</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default VerticallyCenteredModal;
