import Axios from "axios";
import {
  InputGroup,
  Card,
  Form,
  Button,
  Row,
  Col,
  DropdownButton,
  Dropdown,
  Container,
} from "react-bootstrap";

import { useEffect, useState } from "react";
import BugItems from "./BugItems";
import Navbar from "./Navbar";
import Filterbar from "./Filterbar";
import Searchbar from "./Searchbar";
import useAuth from "../hooks/useAuth";

const ManagerInterface = () => {
  const {
    setAvailableProgrammersList,
    bugsList,
    setBugsList,
    fliterList,
    setFilterList,
  } = useAuth();

  const [searchBy, setSearchBy] = useState("");
  const [id, setID] = useState("");
  const [severity, setSeverity] = useState("");
  const [date, setDate] = useState("");

  useEffect(() => {
    setSearchBy("ID");
    Axios.get("http://localhost:3001/getSubmitedBugs").then((response) => {
      setBugsList(response.data);
      setFilterList(response.data);
    });
    Axios.get("http://localhost:3001/getAllAvailableProgrammers").then(
      (response) => {
        setAvailableProgrammersList(response.data);
      }
    );
  }, []);

  useEffect(() => {
    filter();
  }, [severity, id, date]);

  const filter = () => {
    setFilterList(
      searchBy === "Date"
        ? bugsList.filter((bug) => {
            return (
              bug.severity.includes(severity) &&
              (
                bug.month.toString() +
                "/" +
                bug.day.toString() +
                "/" +
                bug.year.toString()
              ).includes(date)
            );
          })
        : bugsList.filter((bug) => {
            return (
              bug.severity.includes(severity) && bug.id.toString().includes(id)
            );
          })
    );
  };

  return (
    <section className="mx-auto" fluid="md" style={{ maxWidth: "800px" }}>
      <Navbar />
      <Row>
        <Col sm="3">
          <Filterbar setSeverity={setSeverity} />
        </Col>
        <Col sm="9">
          <Searchbar
            searchBy={searchBy}
            setSearchBy={setSearchBy}
            id={id}
            setID={setID}
            date={date}
            searchBy={searchBy}
            date={date}
          />
          <BugItems bugsList={fliterList} />
        </Col>
      </Row>
    </section>
  );
};

export default ManagerInterface;
