import { Card, Button, Row } from "react-bootstrap";
import { useState } from "react";

const Filterbar = ({ severity, setSeverity }) => {
  const [currentSeverity, setCurrentSeverity] = useState(severity);

  return (
    <Card border="secondary" bg={"light"}>
      <Card.Header as="h1">Severity</Card.Header>
      <Card.Body>
        <Card.Text as="h5" className="m-2">
          Choose One following to filter the bugs:
        </Card.Text>
        <Row style={{ width: "100%", textAlign: "center" }}>
          <Button
            className="m-2"
            variant="primary"
            value={""}
            style={{ opacity: currentSeverity === "" ? "1" : "0.75" }}
            onClick={() => {
              setSeverity("");
              setCurrentSeverity("");
              console.log(e.target.value)
            }}
          >
            All
          </Button>
          <Button
            className="m-2"
            variant="danger"
            value={"critical"}
            style={{ opacity: currentSeverity === "critical" ? "1" : "0.75" }}
            onClick={(e) => {
              setSeverity("critical");
              setCurrentSeverity("critical");
            }}
          >
            Critical
          </Button>
          <Button
            className="m-2"
            variant="warning"
            value={"moderate"}
            style={{ opacity: currentSeverity === "moderate" ? "1" : "0.85" }}
            onClick={() => {
              setSeverity("moderate");
              setCurrentSeverity("moderate");
            }}
          >
            Moderate
          </Button>
          <Button
            className="m-2"
            variant="success"
            value={"minor"}
            style={{ opacity: currentSeverity === "minor" ? "1" : "0.75" }}
            onClick={() => {
              setSeverity("minor");
              setCurrentSeverity("minor");
            }}
          >
            Minor
          </Button>
        </Row>
      </Card.Body>
    </Card>
  );
};

export default Filterbar;
