import Axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";
import { useState, useEffect } from "react";
import useAuth from "../hooks/useAuth";

const VerticallyCenteredModal = ({
  show,
  onHide,
  programmer,
  severity,
  setProgrammer,
  setSeverity,
}) => {
  const { availableProgrammersList } = useAuth();
  const [currentProgrammer, setCurrentProgrammer] = useState(programmer);
  const [newSeverity, setNewSeverity] = useState(severity);
  const [changing, setChanging] = useState(false);

  useEffect(() => {
    if (programmer === currentProgrammer && severity === newSeverity)
      setChanging(false);
    else {
      setChanging(true);
    }
  }, [newSeverity, currentProgrammer]);

  const clear = () => {
    setCurrentProgrammer("");
    setNewSeverity("");
  };

  const submit = () => {
    Axios.post("http://localhost:3001/signUp", {
      firstName: firstName,
      lastName: lastName,
      user: user,
      email: email,
      password: password,
    })
      .then((res) => {
        console.log(res.data);
        // console.log(res.data.status === 409);
        setShowMessage(true);
        if (res.data.status === 409) {
          setMessage("Email Taken");
          setColor("danger");
        } else if (res.data.status === 200) {
          setMessage("Successfully Registered");
          setColor("success");
          clearForm(e);
        } else {
          setMessage("Someting went wrong, try again later");
          setColor("danger");
        }
      })
      .catch((err) => {
        console.log(err);
        if (!err?.response) {
          setMessage("No Server Response");
          setColor("danger");
        }
      });
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      backdrop={changing ? "static" : true}
      aria-labelledby="contained-modal-title-vcenter"
    >
      <Modal.Header closeButton>
        <Modal.Title>Edit Bugs</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Change the severity:</Form.Label>
            <Form.Select
              onChange={(e) => setNewSeverity(e.target.value)}
              required
            >
              <option value="critical">Critical</option>
              <option value="moderate">Moderate</option>
              <option value="minor">Minor</option>
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Select a programmer for this bug:</Form.Label>
            <Form.Select
              onChange={(e) => setCurrentProgrammer(e.target.value)}
              required
            >
              <option value="N/A">Select a Programmer</option>
              {availableProgrammersList.map((availableProgrammer) => (
                <option key={availableProgrammer.id}>
                  {availableProgrammer.firstName} {availableProgrammer.lastName}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="secondary"
          onClick={() => {
            clear();
            onHide();
          }}
        >
          Close
        </Button>
        <Button
          variant="primary"
          onClick={() => {
            onHide();
            setProgrammer(currentProgrammer);
            setSeverity(newSeverity);
            submit();
          }}
        >
          Save changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default VerticallyCenteredModal;
