import Axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";
import { useState, useEffect } from "react";
import useAuth from "../hooks/useAuth";

const VerticallyCenteredModal = ({
  show,
  onHide,
  id,
  programmer,
  severity,
  setProgrammer,
  setSeverity,
}) => {
  const { availableProgrammersList, setAvailableProgrammersList } = useAuth();
  const [currentProgrammer, setCurrentProgrammer] = useState(programmer);
  const [newSeverity, setNewSeverity] = useState(severity);
  const [changing, setChanging] = useState(false);

  useEffect(() => {
    console.log(
      programmer + " " + currentProgrammer + " " + severity + " " + newSeverity
    );
    if (programmer === currentProgrammer && severity === newSeverity)
      setChanging(false);
    else setChanging(true);
  }, [newSeverity, currentProgrammer]);

  const clear = () => {
    setCurrentProgrammer(programmer);
    setNewSeverity(severity);
    setChanging(false);
  };

  const submit = () => {
    setAvailableProgrammersList(i)
    if (changing)
      Axios.post("http://localhost:3001/editBug", {
        id: id,
        programmer: currentProgrammer,
        severity: newSeverity,
      }).then((res) => {
        console.log(res.data);
      });
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      backdrop={changing ? "static" : true}
      aria-labelledby="contained-modal-title-vcenter"
    >
      <Modal.Header closeButton>
        <Modal.Title>Edit Bug</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Change the severity:</Form.Label>
            <Form.Select
              onChange={(e) => {
                setNewSeverity(e.target.value);
                console.log(e.currentTarget.value);
              }}
              required
            >
              <option value={severity}>Severity</option>
              <option value="critical">Critical</option>
              <option value="moderate">Moderate</option>
              <option value="minor">Minor</option>
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Select a programmer for this bug:</Form.Label>
            <Form.Select
              onChange={(e) => {
                setCurrentProgrammer(e.currentTarget.value);
                console.log(e.currentTarget.value);
              }}
              required
            >
              <option data-id={-1} value={programmer}>
                Select a Programmer
              </option>
              {availableProgrammersList.map((availableProgrammer) => (
                <option
                  key={availableProgrammer.id}
                  data-id={availableProgrammer.id}
                >
                  {availableProgrammer.firstName} {availableProgrammer.lastName}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="secondary"
          onClick={() => {
            clear();
            onHide();
          }}
        >
          Close
        </Button>
        <Button
          variant="primary"
          onClick={() => {
            submit();
            onHide();
            clear();
            setProgrammer(currentProgrammer);
            setSeverity(newSeverity);
          }}
        >
          Save changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default VerticallyCenteredModal;
