import Axios from "axios";
import { Modal, Button, Form, FloatingLabel } from "react-bootstrap";
import { useState, useEffect } from "react";

const ProgrammerModal = ({ show, onHide, id, status, setStatus }) => {
  const [newStatus, setNewStatus] = useState(status);
  const [changing, setChanging] = useState(false);
  const [procedure, setProcedure] = useState("");

  useEffect(() => {
    console.log(newStatus);

    if (status === newStatus) setChanging(false);
    else setChanging(true);
  }, [newStatus]);

  const clear = () => {
    setNewStatus(status);
  };

  const submit = () => {
    if (changing)
      Axios.post("http://localhost:3001/updateStatus", {
        id: id,
        status: newStatus,
      }).then((res) => {
        console.log(res.data);
      });
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      backdrop={changing ? "static" : true}
      keyboard={changing ? false : true}
    >
      <Modal.Header closeButton>
        <Modal.Title>Update Status</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Change the status:</Form.Label>
            <Form.Select
              onChange={(e) => {
                setNewStatus(e.currentTarget.value);
              }}
              required
            >
              <option value={status}>Status</option>
              <option value="Investigating">Investigating</option>
              <option value="Fixing">Fixing</option>
              <option value="Testing">Testing</option>
              <option value="Fixed">Fixed</option>
            </Form.Select>
          </Form.Group>
          <Form.Group>
            <Form.Label>
              Please describe any procedure to replicate this bug
            </Form.Label>
            <FloatingLabel controlId="floatingTextarea2" label="Procedure">
              <Form.Control
                as="textarea"
                placeholder="Enter the URL"
                value={procedure}
                onChange={(e) => setProcedure(e.target.value)}
                style={{ height: "200px" }}
              />
            </FloatingLabel>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="secondary"
          onClick={() => {
            clear();
            onHide();
          }}
        >
          Close
        </Button>
        <Button
          variant="primary"
          onClick={() => {
            submit();
            onHide();
            clear();
            setStatus(newStatus);
          }}
        >
          Save changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ProgrammerModal;
